mapper.update:
UPDATE imgdata


            abnormal=#{abnormal}


            explains=#{explains}


            idname=#{idname}


            violation=#{violation}



     where imgname=#{imgname}

mapper.select:
SELECT DISTINCT  YEAR(updatetime) yyyy FROM `imgdata` where
IFNULL(updatetime,'')

    and userid = #{userid}

SELECT count(id) countNum, MONTH(updatetime) monthNum FROM imgdata where YEAR(updatetime)=#{yyyy}
and userid = #{userid}
GROUP BY MONTH(updatetime) ORDER BY MONTH(updatetime) ASC
